    public static String countAndSay(int n) {
        return SAY[n - 1];
    }

    public static String countAndSayFull(int n) {
        if (n == 1) return "1";
        return rle(getPairs(countAndSay(n - 1)));
    }

    private static int[][] getPairs(String s) {
        //System.out.println("getPairs(" + s + "): {");
        // count same elements:
        int sLength = s.length();
        int count = 0;
        for (int i = 1; i < sLength; i++) if (s.charAt(i - 1) == s.charAt(i)) count++;
        int[][] result = new int[sLength - count][2];
        char latest = s.charAt(0);
        int freq = 1;
        int idx = 0;
        for (int i = 1; i < sLength; i++)
            if (s.charAt(i) == latest) freq++;
            else {
                int[] pair = new int[]{latest - '0', freq};
                //System.out.print(Arrays.toString(pair) + ", ");
                result[idx++] = pair;
                latest = s.charAt(i);
                freq = 1;
            }
        result[idx] = new int[]{latest - '0', freq};
        //System.out.printf("{%d, %d} }%n", latest - '0', freq);
        return result;
    }

    private static String rle(int[][] pairs) {
        StringBuilder sb = new StringBuilder();
        for (int[] pair: pairs)
            //for (int i = 0; i < pair[1]; i++)
            sb.append("" + (char)('0' + pair[1]) + (char)('0' + pair[0]));
        //System.out.println("RLE: " + sb.toString());
        return sb.toString();
    }

    public static void generateArray() {
        // generate the code:
        System.out.println("public static final String[] SAY = {\"1\",");
        String step = "1";
        for (int i = 0; i < 29; i++) {
            step = rle(getPairs(step));
            System.out.println("\"" + step + "\",");
        }
        System.out.println("};");
    }

    public static void stressTest() {
        // now try and go as far as possible:
        // OutOfMemoryError for stepCount 71, around 300M long string
        long prevTime = System.nanoTime();
        String step = "1";
        int stepCount = 0;
        System.out.println("0. 0ms 1 \"1\"");
        while (stepCount < 71) {
            stepCount++;
            step = rle(getPairs(step));
            long current = System.nanoTime();
            long diff = TimeUnit.NANOSECONDS.toMillis(current - prevTime);
            System.out.printf("%s%n%d. %d %d%n", step, stepCount, diff, step.length());
        }

    }

